import React, { Component } from 'react';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';
import './style.scss';
import fashion1 from './images/fashion1.jpeg';
import fashion2 from './images/fashion2.jpeg';
import fashion3 from './images/fashion3.jpeg';
import fashion5 from './images/fashion5.jpg';
import fashion6 from './images/fashion6.jpeg';
import fashion7 from './images/fashion 7.jpeg';
import sus3 from './images/sus3.jpg';


class Hero extends Component {
  constructor(props) {
    super(props);
    this.state = {
      style: {
        width: 1400
			}
    }
  }

  componentDidMount() {
		window.addEventListener('scroll', this.handleScroll);
	};

	componentWillUnmount() {
		window.removeEventListener('scroll', this.handleScroll);
	};


  handleScroll = (event) => {
		let scrollTop = window.scrollY,
        minWidth = 1000,
        width = Math.max(minWidth, 1400 - scrollTop)
        width = width/14
		this.setState({
			style: {
        width: width
      }

		});
	};


  render(){
    return (
      <section className='hero'>
        <div className='hero_container' style={{width:`${this.state.style.width}%`, margin:'auto'}}>
          <Carousel
            showArrows={false}
            showStatus={false}
            showThumbs={false}
            infiniteLoop={true}
            autoPlay={true}
            stopOnHover={false}
            interval={4000}
            transitionTime={400}>
              <div>
                  <img src={fashion1} className='image'/>
                  <p className="legend" style={{color:'lightgreen'}}>Legend one says that i=this is icons for the best icon 1</p>
              </div>
              <div>
                  <img src={fashion7} className='image'/>
                  <p className="legend" style={{color:'white !important'}}>Legend is about sex shop in the resturant and fuck the beautiful girl 2</p>
              </div>
              <div>
                  <img src={sus3} className='image'/>
                  <p className="legend" style={{color:'white'}}>Legend is nothing just another sex with estonian girls in the street3</p>
              </div>
          </Carousel>
        </div>
      </section>
    );
  }
}

export default Hero;
